mapper.select:
select
       count(1)
from t_trade t
                left join sysuser u on u.id = t.user_id
                left join t_symbol s on s.id = t.symbol_id
where 1 = 1
    
      and u.account = #{account,jdbcType=VARCHAR}
    
    
      and t.side = = #{side,jdbcType=VARCHAR}
    
    
      and t.symbol_id = #{side,jdbcType=INTEGER}

select
        t.side, t.price, t.amount, t.fee, t.time, s.name, u.acount
from
     t_trade t
            left join sysuser u on u.id = t.user_id
            left join t_symbol s on s.id = t.symbol_id
where 1 = 1
          
            and u.account = #{account,jdbcType=VARCHAR}
          
          
            and t.side = = #{side,jdbcType=VARCHAR}
          
          
            and t.symbol_id = #{side,jdbcType=INTEGER}
          
limit #{pageSize, jdbcType=INTEGER} offset #{start,jdbcType=INTEGER}
